vite.config.ts.content:
import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { createServer } from "http";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

// For Vercel deployment, we need to export a handler
// But we also need to maintain compatibility with local development

let serverPromise: Promise<ReturnType<typeof createServer>> | null = null;

// Initialize routes
try {
  serverPromise = registerRoutes(app);
  serverPromise.catch((error) => {
    console.error("Failed to register routes:", error);
  });
} catch (error) {
  console.error("Failed to initialize routes:", error);
}

app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
  const status = err.status || err.statusCode || 500;
  const message = err.message || "Internal Server Error";

  res.status(status).json({ message });
  // Don't throw in production as it might crash the server
  if (process.env.NODE_ENV !== "production") {
    throw err;
  }
});

// Export the Express app for local development
export default app;

// For local development, we still need to start the server
if (!process.env.VERCEL) {
  // For local development
  (async () => {
    try {
      // Wait for routes to be registered
      const server = await serverPromise!;

      // Setup static files and Vite in development
      if (process.env.NODE_ENV === "development") {
        await setupVite(app, server);
      } else {
        serveStatic(app);
      }

      const port = parseInt(process.env.PORT || '5000', 10);
      server.listen(port, () => {
        log(`serving on port ${port}`);
      });
    } catch (error) {
      console.error("Failed to start server:", error);
    }
  })();
}

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { redirectMiddleware } from "./redirects";
import express from "express";
import path from "path";

export async function registerRoutes(app: Express): Promise<Server> {
  // Handle redirects
  app.use(redirectMiddleware);

  // Serve static HTML files from articles and roms directories
  app.use('/articles', express.static(path.join(process.cwd(), 'articles'), {
    extensions: ['html'],
    setHeaders: (res) => {
      res.setHeader('Cache-Control', 'no-cache');
    }
  }));

  app.use('/roms', express.static(path.join(process.cwd(), 'roms'), {
    extensions: ['html'],
    setHeaders: (res) => {
      res.setHeader('Cache-Control', 'no-cache');
    }
  }));

  // Get all ROM data
  app.get("/api/rom-data", async (req, res) => {
    try {
      const data = await storage.getRomData();
      res.json(data);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch ROM data" });
    }
  });

  // Get all categories
  app.get("/api/categories", async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch categories" });
    }
  });

  // Get specific category
  app.get("/api/categories/:id", async (req, res) => {
    try {
      const category = await storage.getCategory(req.params.id);
      if (!category) {
        return res.status(404).json({ error: "Category not found" });
      }
      res.json(category);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch category" });
    }
  });

  // Get games with optional filters
  app.get("/api/games", async (req, res) => {
    try {
      const {
        categoryId,
        search,
        sortBy,
        page = '1',
        limit = '20'
      } = req.query;

      const result = await storage.getGames({
        categoryId: categoryId as string,
        search: search as string,
        sortBy: sortBy as 'downloads' | 'rating' | 'year' | 'title',
        page: parseInt(page as string),
        limit: parseInt(limit as string),
      });

      res.json(result);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch games" });
    }
  });

  // Get specific game
  app.get("/api/games/:id", async (req, res) => {
    try {
      const game = await storage.getGame(req.params.id);
      if (!game) {
        return res.status(404).json({ error: "Game not found" });
      }
      res.json(game);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch game" });
    }
  });

  // Get popular games
  app.get("/api/popular", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 4;
      const games = await storage.getPopularGames(limit);
      res.json(games);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch popular games" });
    }
  });

  // Get all consoles
  app.get("/api/consoles", async (req, res) => {
    try {
      const consoles = await storage.getConsoles();
      res.json(consoles);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch consoles" });
    }
  });

  // Get ROMs with pagination and filters
  app.get("/api/roms", async (req, res) => {
    try {
      const {
        console,
        category,
        search,
        sortBy,
        page = '1',
        limit = '20'
      } = req.query;

      const result = await storage.getGames({
        console: console as string,
        category: category as string,
        search: search as string,
        sortBy: sortBy as 'downloads' | 'rating' | 'year' | 'title',
        page: parseInt(page as string),
        limit: parseInt(limit as string),
      });

      res.json(result);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch ROMs" });
    }
  });

  // Get ROM by console and slug
  app.get("/api/roms/:console/:slug", async (req, res) => {
    try {
      const { console: consoleParam, slug } = req.params;
      console.log(`Server route: Searching for game with console: ${consoleParam}, slug: ${slug}`);
      // For now, just pass the slug to getGameBySlug
      const game = await storage.getGameBySlug(consoleParam, slug);
      if (!game) {
        console.log(`Server route: Game not found for console: ${consoleParam}, slug: ${slug}`);
        return res.status(404).json({ error: "ROM not found" });
      }
      console.log(`Server route: Found game: ${game.title}`);
      res.json(game);
    } catch (error) {
      console.error("Server route: Failed to fetch ROM:", error);
      res.status(500).json({ error: "Failed to fetch ROM" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

