import { useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useRoute, Link } from "wouter";
import { ArrowLeft, Download } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  Breadcrumb,
  BreadcrumbList,
  BreadcrumbItem,
  BreadcrumbLink,
  BreadcrumbPage,
  BreadcrumbSeparator,
} from "@/components/ui/breadcrumb";
import type { GameData } from "@shared/schema";
import { AdSense } from "@/components/AdSense";

export default function RomDetail() {
  const [match, params] = useRoute("/roms/:console/:slug");

  const { data: game, isLoading } = useQuery<GameData>({
    queryKey: ["/api/roms", params?.console, params?.slug],
    enabled: !!(params?.console && params?.slug),
    queryFn: async () => {
      const response = await fetch(`/api/roms/${params?.console}/${params?.slug}`);
      if (!response.ok) throw new Error('Failed to fetch ROM');
      return response.json();
    },
  });

  // Set SEO meta tags
  useEffect(() => {
    if (game) {
      document.title = `${game.title} Download - ${game.platform} Roms - EmulatorGames.Net`;

      const metaDescription = document.querySelector('meta[name="description"]');
      if (metaDescription) {
        metaDescription.setAttribute('content', getGameDescription(game));
      }
    }
  }, [game]);

  if (!match || !params?.console || !params?.slug) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center py-12">
          <h1 className="text-2xl font-bold mb-4">ROM not found</h1>
          <p className="text-muted-foreground mb-4">The requested ROM could not be found.</p>
          <Link to="/roms">
            <Button data-testid="button-back-roms">Browse ROMs</Button>
          </Link>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex items-center space-x-4 mb-8">
          <Skeleton className="h-10 w-10" />
          <div className="space-y-2">
            <Skeleton className="h-10 w-96" />
            <Skeleton className="h-6 w-64" />
          </div>
        </div>
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          <div className="lg:col-span-1">
            <Skeleton className="w-full aspect-[3/4] rounded-lg mb-4" />
            <Skeleton className="w-full h-64 rounded-lg" />
          </div>
          <div className="lg:col-span-2 space-y-6">
            <Skeleton className="w-full h-48 rounded-lg" />
            <Skeleton className="w-full h-64 rounded-lg" />
          </div>
        </div>
      </div>
    );
  }

  if (!game) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center py-12">
          <h1 className="text-2xl font-bold mb-4">ROM not found</h1>
          <p className="text-muted-foreground mb-4">The requested ROM could not be found.</p>
          <Link to="/roms">
            <Button data-testid="button-back-roms">Browse ROMs</Button>
          </Link>
        </div>
      </div>
    );
  }

  const renderStars = (rating: number) => {
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 >= 0.5;
    const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);

    return (
      <span className="star-rating text-2xl">
        {"★".repeat(fullStars)}
        {hasHalfStar && "☆"}
        {"☆".repeat(emptyStars)}
      </span>
    );
  };

  const handleDownload = () => {
    if (game.downloadUrl) {
      // Open download page in same tab
      window.location.href = `/roms/${params.console}/${params.slug}/download`;
    }
  };

  const getGameDescription = (game: GameData): string => {
    // If we have a description, use it
    if (game.description) {
      return game.description;
    }

    // Generate description following the requested format
    const downloadsFormatted = game.downloads >= 1000 
      ? `${(game.downloads / 1000).toFixed(0)}K` 
      : game.downloads.toString();

    const categoryDesc = game.category.toLowerCase();

    const description = `Download ${game.title} Rom is available to play for ${game.platform} console. This game is the ${game.region} version at EmulatorGames.Net exclusively. Download ${game.title} ROM and use it with an emulator and Play this ${game.platform} game on desktop PC, mobile, and tablets in maximum quality. This ${categoryDesc} title offers ${categoryDesc} gameplay that defined gaming in ${game.year}. With over ${downloadsFormatted} downloads, this is a highly popular classic that continues to captivate retro gaming enthusiasts. The ROM file is compatible with ${game.platform} emulators and provides an authentic gaming experience true to the original ${game.year} release.`;

    return description;
  };

  return (
    <div className="container mx-auto px-4 py-8 space-y-8">
