          </BreadcrumbItem>
          <BreadcrumbSeparator />
          <BreadcrumbItem>
            <BreadcrumbLink asChild>
              <Link to="/roms" data-testid="link-breadcrumb-roms">ROMs</Link>
            </BreadcrumbLink>
          </BreadcrumbItem>
          <BreadcrumbSeparator />
          <BreadcrumbItem>
            <BreadcrumbLink asChild>
              <Link to={`/roms/${game.categoryId}`} data-testid="link-breadcrumb-platform">{game.platform}</Link>
            </BreadcrumbLink>
          </BreadcrumbItem>
          <BreadcrumbSeparator />
          <BreadcrumbItem>
            <BreadcrumbPage data-testid="text-breadcrumb-current">{game.title}</BreadcrumbPage>
          </BreadcrumbItem>
        </BreadcrumbList>
      </Breadcrumb>

      {/* ROM Header */}
      <div className="mb-8">
        <h1 className="text-4xl font-bold" data-testid="text-rom-title">
          {game.title}
        </h1>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* ROM Image and Download */}
        <div className="lg:col-span-1">
          <img 
            src={game.image}
            alt={`${game.title} game cover`}
            className="w-full rounded-lg shadow-lg mb-4"
            data-testid="img-rom-cover"
          />

          {/* Download Section */}
          <Card>
            <CardHeader>
              <CardTitle data-testid="text-download-info">Download Information</CardTitle>
