        'cd-i': 'CD-i',
        'commodore-64-preservation': 'C64 Preservation',
        'commodore-64-tapes': 'C64 Tapes',
        'commodore-vic-20': 'Commodore VIC-20',
        'gameboy-color': 'GBC',
        'game-boy-color': 'GBC',
        'gameboy': 'GB',
        'game-boy': 'GB',
        'game-boy-advance': 'GBA',
        'gba': 'GBA',
        'gamecube': 'GameCube',
        'game-gear': 'Game Gear',
        'sega-game-gear': 'Game Gear',
        'gce-vectrex': 'Vectrex',
        'vectrex': 'Vectrex',
        'magnavox-odyssey-2': 'Magnavox Odyssey 2',
        'msx-2': 'MSX2',
        'msx2': 'MSX2',
        'msx': 'MSX',
