        'neo-geo-pocket': 'NGP',
        'neo-geo': 'Neo Geo',
        'new-geo': 'Neo Geo',
        'nintendo-64': 'N64',
        'n64': 'N64',
        'nintendo-ds': 'NDS',
        'nds': 'NDS',
        'nintendo-wii': 'Wii',
        'wii': 'Wii',
        'nintendo-wii-u': 'Wii U',
        'wii-u': 'Wii U',
        'nintendo-switch': 'Switch',
        'switch': 'Switch',
        'nes': 'NES',
        'nintendo-entertainment-system': 'NES',
        'snes': 'SNES',
        'super-nintendo': 'SNES',
        'famicom': 'Famicom',
        'pc-fx': 'PC-FX',
        'playstation': 'PSX',
        'playstation-1': 'PSX',
        'psx': 'PSX',
